<?phpglobal $listingpro_options;$lp_detail_page_styles  =   $listingpro_options['lp_detail_page_styles'];$plan_id = listing_get_metabox_by_ID('Plan_id',get_the_ID());if(!empty($plan_id)){    $plan_id = $plan_id;}else{    $plan_id = 'none';}$faqs_show = get_post_meta( $plan_id, 'listingproc_faq', true );if($plan_id=="none"){    $faqs_show = 'true';}$faqs = listing_get_metabox('faqs');if( $faqs_show == true && !empty( $faqs ) ){   $faq        =   $faqs['faq'];   $faq        =   array_filter($faq);   $faqans     =   $faqs['faqans'];   $faq_count  =   '';   if( is_array($faq) )   {       $faq_count  =   count($faq);   }    for ($i = 1; $i <= (count($faq)); $i++):        if( !empty( $faq[$i] ) && !empty( $faqans[$i] ) ):            if( $i == 1 )            {                if( $lp_detail_page_styles != 'lp_detail_page_styles2' || $lp_detail_page_styles != 'lp_detail_page_styles1' ):                    echo '<h4 class="lp-detail-section-title">'.esc_html__( 'FAQs', 'listingpro' ).'</h4>';                endif;                echo '<div class="lp-listing-faqs" id="accordion" role="tablist" aria-multiselectable="true">';            }        ?>        <div class="lp-listing-faq <?php if( $i == count( $faq ) ){echo 'last';} ?> ">            <div class="faq-heading" role="tab">                <h4 class="faq-title" role="button" data-toggle="collapse" data-parent="#accordion" href="#collapse<?php echo $i; ?>" aria-expanded="true" aria-controls="collapse<?php echo $i; ?>">                    <a>                        <span><?php echo esc_html__('Q', 'listingpro'); ?></span> <?php echo esc_html($faq[$i]); ?>                        <i class="more-less glyphicon <?php if( $i == 1 ){ echo 'glyphicon-minus'; }else{ echo 'glyphicon-plus'; } ?>"></i>                    </a>                </h4>                <div class="clearfix"></div>            </div>            <div id="collapse<?php echo $i; ?>" class="collapse <?php if( $i == 1 ){ echo 'in'; } ?> faq-answer" role="tabpanel" aria-labelledby="heading<?php echo $i; ?>">                <div class="answer-body">                    <p>                        <?php echo nl2br(do_shortcode($faqans[$i]), false); ?>                    </p>                </div>            </div>        </div><?php        if( count($faq) == $i )        {            echo '</div>';        }            endif;    endfor;}?>